CREATE TABLE strada (
  nume VARCHAR2(255) NOT NULL
);
/
CREATE TABLE oras (
  nume VARCHAR2(255) NOT NULL,
  strada VARCHAR2(255) NOT NULL,
  distanta INT
)
/
CREATE TABLE strazi (
  strada1 VARCHAR2(255) NOT NULL,
  strada2 VARCHAR2(255) NOT NULL,
  distanta INT
)

/
CREATE TABLE clienti (
  id_client INT NOT NULL PRIMARY KEY,
  nume_utilizator VARCHAR2(255) NOT NULL,
  nume VARCHAR2(255) NOT NULL,
  prenume VARCHAR2(255) NOT NULL,
  sex VARCHAR2(255) NOT NULL,
  telefon VARCHAR(255) NOT NULL,
  oras VARCHAR2(255) NOT NULL,
  adresa VARCHAR2(255) NOT NULL,
  email VARCHAR2(255) NOT NULL,
  parola VARCHAR2(255) NOT NULL,
  data_inregistrare DATE
)
/
CREATE TABLE restaurant (
  id_restaurant INT NOT NULL PRIMARY KEY,
  nume VARCHAR2(100),
  adresa VARCHAR2(100),
  oras VARCHAR(100)
)
/
CREATE TABLE categorie (
  id_categorie VARCHAR2(10) NOT NULL PRIMARY KEY,
  alergeni VARCHAR2 (100) 
)
/
CREATE TABLE combinari (
  id_combinari INT NOT NULL PRIMARY KEY,
  id_produs INT NOT NULL,
  id_restaurant INT NOT NULL,
  pret FLOAT NOT NULL
)
/
CREATE TABLE produse (
  id_produs INT NOT NULL PRIMARY KEY,
  nume VARCHAR2(100) NOT NULL,
  id_categorie VARCHAR2(10) NOT NULL
)
/
CREATE TABLE comanda (
  id_comanda INT NOT NULL,
  id_client INT NOT NULL,
  id_combinari INT NOT NULL,
  comentariu VARCHAR2(255),
  FOREIGN KEY (id_client) REFERENCES clienti(id_client),
  FOREIGN KEY (id_combinari) REFERENCES combinari(id_combinari)
)
/
CREATE TABLE livrari (
  id_livrare INT NOT NULL PRIMARY KEY,
  id_client INT NOT NULL,
  total float NOT NULL,
  data_livrare timestamp ,
  FOREIGN KEY (id_client) REFERENCES clienti(id_client)
)
/
CREATE INDEX generare_combinari
ON combinari (id_produs, id_restaurant);
/
CREATE INDEX afisare_produs
ON produse (id_categorie);
/
CREATE INDEX generare_restaurant
ON restaurant (oras, id_restaurant);
/
