            THEN
              v_nume := lista_paste(TRUNC(DBMS_RANDOM.VALUE(0,lista_paste.count))+1);
              v_pret := (TRUNC(DBMS_RANDOM.VALUE(25,60),2)+1);
         ELSE
              v_nume := lista_deserturi(TRUNC(DBMS_RANDOM.VALUE(0,lista_deserturi.count))+1);
              v_pret := (TRUNC(DBMS_RANDOM.VALUE(30,80),2)+1);
              
          END IF;
          SELECT id_produs into v_id_inserare from produse where nume = v_nume;
          SELECT COUNT(*) INTO v_contor from combinari where id_produs = v_id_inserare and id_restaurant = v_restaurant;
          EXIT WHEN v_contor = 0;
       END LOOP;
       
       insert into combinari values (v_i, v_id_inserare, v_restaurant, v_pret);
       v_reducere := TRUNC(DBMS_RANDOM.VALUE(1,10)) || '%';
       END LOOP;
       insert into categorie values('Paste', 'Usturoi');
       insert into categorie values('Pizza', NULL);
       insert into categorie values('Deserturi', 'Lapte');       
       FOR v_i IN 1..80000 LOOP
          SELECT COUNT(*) INTO v_contor from clienti;
          LOOP
              v_norocos := TRUNC(DBMS_RANDOM.VALUE(1,v_contor));
              SELECT oras into v_oras from clienti where id_client = v_norocos;
              SELECT COUNT(*) INTO v_nr_restaurante from restaurant where v_oras = oras; /* sunt restaurante in acel oras */
              EXIT WHEN v_nr_restaurante != 0;
           END LOOP;
           
           SELECT COUNT(*) INTO v_nr_restaurante from restaurant;
           LOOP
              v_restaurant :=  TRUNC(DBMS_RANDOM.VALUE(1,v_nr_restaurante));
              SELECT COUNT(*) INTO v_contor from restaurant where v_oras = oras and id_restaurant = v_restaurant;  /* selectez restaurant din acel oras */
              EXIT WHEN v_contor != 0;
           END LOOP;
           SELECT COUNT(*) INTO v_nr_produse from produse; 
           
           v_for :=  TRUNC(DBMS_RANDOM.VALUE(1,10));
           
           FOR v_j IN 1..v_for LOOP               
              LOOP      
                v_produs :=  TRUNC(DBMS_RANDOM.VALUE(1,v_nr_produse));
                SELECT COUNT(*) INTO v_contor from produse p
                JOIN combinari c on c.id_produs = p.id_produs  and id_restaurant = v_restaurant and c.id_produs = v_produs;
                EXIT WHEN v_contor != 0;    
              END LOOP;
              select id_categorie into v_categorie from produse where id_produs = v_produs;
              SELECT pret INTO v_pret from combinari where  id_produs = v_produs and id_restaurant = v_restaurant;
               
           IF (v_categorie = 'Pizza')
               THEN
                 
               v_comm := lista_coment1(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment1.count))+1) || ' ' || lista_coment2(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment2.count))+1) || ' ' || lista_coment_pizza(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment_pizza.count))+1);
           
             ELSIF(v_categorie = 'Paste')
                THEN
               v_comm := lista_coment1(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment1.count))+1) || ' ' || lista_coment2(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment2.count))+1) || ' ' || lista_coment_paste(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment_paste.count))+1);

             ELSE
               v_comm := lista_coment1(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment1.count))+1) || ' ' || lista_coment2(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment2.count))+1) || ' ' || lista_coment_desert(TRUNC(DBMS_RANDOM.VALUE(0,lista_coment_desert.count))+1);

              END IF;
             SELECT id_combinari into v_wall from combinari where id_produs = v_produs and id_restaurant = v_restaurant;
             insert into comanda values(v_i, v_norocos, v_wall, v_comm);
             
           END LOOP;
           SELECT adresa INTO v_adresa from clienti where  id_client = v_norocos;
           v_scad_data := TRUNC(DBMS_RANDOM.VALUE(1,365));
           insert into livrari values(v_i, v_norocos, v_adun, localtimestamp - v_scad_data);     
       END LOOP;
       
       FOR v_i IN 1..(lista_orase.count - 1) LOOP
          FOR v_j IN 1..(lista_strazi.count - 1) LOOP
          v_scad_data := TRUNC(DBMS_RANDOM.VALUE(1,15));
          insert into oras values (lista_orase(v_i), lista_strazi(v_j), v_scad_data);
          END LOOP;
       END LOOP;
       
      FOR v_i IN 1..(lista_strazi.count ) LOOP
          FOR v_j IN 1..v_i LOOP
          if v_i = v_j then 
           insert into strazi values (lista_strazi(v_i), lista_strazi(v_j), 0);
          else
          v_scad_data := TRUNC(DBMS_RANDOM.VALUE(1,10));
           insert into strazi values (lista_strazi(v_i), lista_strazi(v_j), v_scad_data);
           insert into strazi values ( lista_strazi(v_j), lista_strazi(v_i), v_scad_data);
          END IF;
          END LOOP;
           v_scad_data := TRUNC(DBMS_RANDOM.VALUE(1,10));
           insert into strazi values ('sediu', lista_strazi(v_i), v_scad_data);
           insert into strazi values ( lista_strazi(v_i), 'sediu', v_scad_data);
       END LOOP;

       
      FOR v_i IN 1..(lista_strazi.count - 1) LOOP
          insert into strada values (lista_strazi(v_i));
       END LOOP;
END;
